 <div style="overflow-y:auto; overflow-x:hidden; height:280px"> <table class="gridlist"> <tbody> <?php foreach ((array)$this->_vars['allPay'] as $this->_vars['key'] => $this->_vars['item']){  if( $this->_vars['item']['pay_type'] =='other' ){ ?> <tr><?php $this->_vars["name"]=urlencode($this->_vars['item']['pay_name']); ?> <td width="15%"><img height="25" src="http://esb.shopex.cn/payments/logos/<?php echo $this->_vars['item']['pay_ident']; ?>.gif"></td> <td width="15%"><strong><?php echo $this->_vars['item']['pay_name']; ?></strong></td> <td width="56%"><?php echo $this->_vars['item']['pay_intro']; ?></td> <?php if( $this->_vars['item']['disable'] =='true' ){ ?> <td><button type="button" onclick="<?php echo "W.page('index.php?ctl=trading/payment&act=do_install&p[0]={$this->_vars['item']['pay_ident']}',{onRequest:function(){window.mainwidth=$('main').getStyle('width')}})";?>" class="btn"><span><span><img src="images/transparent.gif" class="imgbundle icon" style="width:14px;height:14px;background-position:0 -88px;" />启用</span></span></button></td> <?php }else{ ?> <td><button type="button" onclick="<?php echo "W.page('index.php?ctl=trading/payment&act=editPayment&p[0]={$this->_vars['item']['id']}&p[1]={$this->_vars['name']}&p[2]={$this->_vars['item']['pay_ident']}',{onRequest:function(){window.mainwidth=$('main').getStyle('width')}})";?>" class="btn"><span><span><img src="images/transparent.gif" class="imgbundle icon" style="width:14px;height:14px;background-position:0 -88px;" />添加</span></span></button></td> <?php } ?> </tr> <?php }  } ?> </tbody> </table> </div>