<?php if(!function_exists('tpl_function_pager')){ require(CORE_DIR.'/include_v5/smartyplugins/function.pager.php'); } if(!function_exists('tpl_modifier_userdate')){ require(CORE_DIR.'/include_v5/smartyplugins/modifier.userdate.php'); } ?> <div class="ArtListWrap"> <?php echo tpl_function_pager(array('data' => $this->_vars['pager']), $this);?> <h2><?php echo $this->_vars['cat']['cat_info']; ?></h2> <ul class="list"> <?php foreach ((array)$this->_vars['articles'] as $this->_vars['key'] => $this->_vars['article']){ ?> <li><span class="date">[<?php echo tpl_modifier_userdate($this->_vars['article']['uptime']); ?>]</span><a href="<?php echo $this->_env_vars['base_url'],"article-{$this->_vars['article']['article_id']}",(((is_numeric($this->_vars['article']['article_id']) && 'index'==index) || !index)?'':'-'.index),'.html';?>"><?php echo $this->_vars['article']['title']; ?></a></li> <?php } ?> </ul> <?php echo tpl_function_pager(array('data' => $this->_vars['pager']), $this);?> </div>