<?php if(!function_exists('tpl_modifier_storager')){ require(CORE_DIR.'/include_v5/smartyplugins/modifier.storager.php'); } if(!function_exists('tpl_modifier_escape')){ require(CORE_DIR.'/include_v5/smartyplugins/modifier.escape.php'); } if(!function_exists('tpl_modifier_number')){ require(CORE_DIR.'/include_v5/smartyplugins/modifier.number.php'); } if(!function_exists('tpl_function_goodsmenu')){ require(CORE_DIR.'/include_v5/smartyplugins/function.goodsmenu.php'); } $CURRENCY = &$this->system->loadModel('system/cur'); ?><div class="ItemsWarp clearfix"> <?php $this->_vars['zindex']='1000';  foreach ((array)$this->_vars['products'] as $this->_vars['product']){ ?> <div class="items-list <?php echo $this->_vars['mask_webslice']; ?>" product="<?php echo $this->_vars['product']['goods_id']; ?>" id="pdt-<?php echo $this->_vars['product']['goods_id']; ?>"> <table width="100%" border="0" cellspacing="0" cellpadding="0"> <tr> <td class="goodpic" valign="middle" style='<?php if( $this->system->getConf('site.thumbnail_pic_width') !=0 && $this->system->getConf('site.thumbnail_pic_height') !=0 ){ ?> width:<?php echo $this->system->getConf('site.thumbnail_pic_width'); ?>px;<?php } ?>'> <a target="_blank" style='<?php if( $this->system->getConf('site.thumbnail_pic_width') !=0 && $this->system->getConf('site.thumbnail_pic_height') !=0 ){ ?> width:<?php echo $this->system->getConf('site.thumbnail_pic_width'); ?>px;height:<?php echo $this->system->getConf('site.thumbnail_pic_height'); ?>px;<?php } ?>' href="<?php echo $this->_env_vars['base_url'],"product-{$this->_vars['product']['goods_id']}",(((is_numeric($this->_vars['product']['goods_id']) && 'index'=="index") || !"index")?'':'-'."index"),'.html';?>"> <img <?php if( $this->system->getConf('site.thumbnail_pic_width') !=0 ){ ?> width="<?php echo $this->system->getConf('site.thumbnail_pic_width'); ?>px"<?php }  if( $this->system->getConf('site.thumbnail_pic_height') !=0 ){ ?>height="<?php echo $this->system->getConf('site.thumbnail_pic_height'); ?>px"<?php } ?> src="<?php echo tpl_modifier_storager(((isset($this->_vars['product']['thumbnail_pic']) && ''!==$this->_vars['product']['thumbnail_pic'])?$this->_vars['product']['thumbnail_pic']:$this->system->getConf('site.default_thumbnail_pic'))); ?>" alt="<?php echo tpl_modifier_escape($this->_vars['product']['name'],html); ?>"/> </a> </td> <td width='10px;'>&nbsp;</td> <td class="goodinfo"> <h6><a href="<?php echo $this->_env_vars['base_url'],"product-{$this->_vars['product']['goods_id']}",(((is_numeric($this->_vars['product']['goods_id']) && 'index'=="index") || !"index")?'':'-'."index"),'.html';?>" title="<?php echo tpl_modifier_escape($this->_vars['product']['name'],html); ?>" class="entry-title" target="_blank"><?php echo tpl_modifier_escape($this->_vars['product']['name'],"html"); ?></a></h6> <?php echo $this->_vars['product']['brief']; ?> </td> <td class="price_button" width="250"> <ul> <li><span class="price1"><?php echo $CURRENCY->changer($this->_vars['product']['price']); ?></span><?php if( $this->_vars['product']['mktprice'] && $this->_vars['setting']['mktprice'] ){ ?><span class="mktprice1">市场价: <?php echo $CURRENCY->changer($this->_vars['product']['mktprice']); ?></span><?php } ?></li> <?php if( $this->_vars['product']['mktprice'] > $this->_vars['product']['price'] && $this->_vars['setting']['mktprice'] && $this->_vars['setting']['saveprice'] > 0 ){ ?> <li style="padding-left:4px; padding-top:8px;"><span class="saveprice1"> <?php if( $this->_vars['setting']['saveprice'] == 1 ){ ?>节省：<?php echo $CURRENCY->changer($this->_vars['product']['mktprice']-$this->_vars['product']['price']);  }elseif( $this->_vars['setting']['saveprice'] == 2 ){ ?>优惠:<?php echo tpl_modifier_number($this->_vars['product']['price']/$this->_vars['product']['mktprice']*100,'2'); ?>% <?php }elseif( $this->_vars['setting']['saveprice'] == 3 ){  echo tpl_modifier_number($this->_vars['product']['price']/$this->_vars['product']['mktprice']*10,'1'); ?>折 <?php } ?></span></li> <?php } ?> <!--<li class="intro rank-<?php echo ((isset($this->_vars['product']['rank']) && ''!==$this->_vars['product']['rank'])?$this->_vars['product']['rank']:3); ?>">Rank <?php echo ((isset($this->_vars['product']['rank']) && ''!==$this->_vars['product']['rank'])?$this->_vars['product']['rank']:3); ?></li>--> </ul> <ul class="button"> <?php echo tpl_function_goodsmenu(array('product' => $this->_vars['product'],'setting' => $this->_vars['setting'],'login' => $this->_vars['LOGIN'],'zindex' => $this->_vars['zindex']--), $this);?> <li class="btncmp"> <a href="javascript:void(0)" onclick="gcompare.add({gid:'<?php echo $this->_vars['product']['goods_id']; ?>',gname:'<?php echo tpl_modifier_escape(addslashes($this->_vars['product']['name']),html); ?>',gtype:'<?php echo $this->_vars['product']['type_id']; ?>'});" class="btncmp" title="商品对比"> 商品对比 </a> </li> </ul> </td> </tr> </table> </div> <?php } ?> </div> 